import React,{useContext} from "react";
import {View,StyleSheet} from "react-native";
import {Text} from "react-native-elements";
import {SafeAreaView,withNavigationFocus} from "react-navigation";
import Map from "../Components/Map";
import _mockLocation from "../_mockLocation";
import {Context as LocationContext} from "../context/LocationContext";
import useLocation from "../Hooks/useLocation";
import AuthForm from "../Components/TrackForm";
import TrackForm from "../Components/TrackForm";

const TrackCreateScreen = ({isFocused}) => {
    const {addLocation} = useContext(LocationContext);
    const [err] = useLocation(isFocused,(location)=>{addLocation(location)});
    return (
        <View>
            <SafeAreaView forceInset={{top:"always"}}>
        <Text h2>TrackCreateScreen</Text>
        <Map/>
        {err?<Text>Please provide permission</Text> : null}
        <TrackForm/>
        </SafeAreaView>
        </View>
    )
};

TrackCreateScreen.navigationOptions = ({navigation}) => {
    return {
        header : null
    }
}
const styles = StyleSheet.create(
    {

    }
);

export default withNavigationFocus(TrackCreateScreen);
