import {AsyncStorage} from "react-native";
import CreateDataContext from "./CreateDataContext";
import trackerApi from "../Api/trackerApi";
import {navigate} from "../Navigation/navigationRef";

const authReducer = (state, action) => {
    switch(action.type) {
        case "add_error":
            return{...state,errorMessage:action.payload};
        case "signup":
            return{errorMessage:action.payload.Message,token:action.payload.token}
        case "clear_error_message":
            return{...state,errorMessage:""}
        default:
            return state;
    }
};
const tryLocalSingin = (dispatch) => {
    return async () => {
        const token = await AsyncStorage.getItem("token");
        if(token){
            dispatch({type:"signup",
        payload:token})
    }
}
}
const errorMessage = (dispatch) => {
    return ()=> {
        dispatch({type:"clear_error_message"})
    }
}
const signup = (dispatch)=> {
    return async ({Email,Name,Password}) => {
        try {
            const response = await trackerApi.post("/Signup", {Email,Name,Password});
            dispatch({type:"signup",
            payload:{token:response.data.Token,
                Message:response.data.Message}});
            console.log(response.data)
            await AsyncStorage.setItem("token",response.data.Token)
            navigate("trackList");
            
        }
        catch(e){
            console.log(e)
            dispatch({type:"add_error",payload:"Something went wrong with Signup"})

        }
    };
};

const signin = (dispatch)=> {
    return async ({Email, Password}) => {
        try{
            const response = await trackerApi.post("/Login",{Email,Password});
            dispatch({
                type:"signup",
                payload:{token:response.data.Token,
                    Message:response.data.Message}
            });
            console.log(response.data)
            await AsyncStorage.setItem("token",response.data.Token)
            navigate("trackList");
        }  
        catch(e){
            console.log(e)
            dispatch({type:"add_error",payload:"Something went wrong with Signin"})                        
        }      
    };
};

const signout = (dispatch)=> {
    return () => {
        
    };
};

export const {Provider, Context} = CreateDataContext(
    authReducer,
    {signup,signin,signout,errorMessage},
    {token:null,errorMessage:""}
    );
